mapper.select:
select pr.Reviewdate,pr.Review,pr.Reviewstatusname,pr.Reviewname,pr.Reviewlevel,pr.Createdate,pr.Reviewhandintime
 from personal_reviewall pr,personaluser p
 where pr.PersonalUserid=p.PersonalUserid and p.Phonenumber=#{Phonenumber}

select *
from personal_reviewall
where PersonalUserid=#{PersonalUserid} and PersonalreviewID=#{PersonalreviewID};
